<?phpclass Home_model extends CI_Model{	function insert()	{	$this->load->helper('file');	$data['title']=$this->input->post("title");	$data['artikel']=$this->input->post("artikel");		$data['ex1']=$this->input->post("expansion");	$data['ex2']=$this->input->post("expansion2");	$title = $this->input->post("expansion");mkdir("./application/modules/".$data['ex1'], 0700);mkdir("./application/modules/".$data['ex1']."/controllers", 0700);mkdir("./application/modules/".$data['ex1']."/models", 0700);mkdir("./application/modules/".$data['ex1']."/views", 0700);mkdir("./application/modules/".$data['ex2'], 0700);mkdir("./application/modules/".$data['ex2']."/controllers", 0700);mkdir("./application/modules/".$data['ex2']."/models", 0700);mkdir("./application/modules/".$data['ex2']."/views", 0700);$controller = '<?php class '.$title.' extends CI_Controller{function index(){$data["container"] = $this->load->view("container");$this->load->view("'.$title.'_view",$data);}}';$model = '<?php class '.$title.' extends CI_Model{}';$view = "VIEW";write_file('./application/modules/'.$data['ex1'].'/views/'.$title.'_view.php',$view);write_file('./application/modules/'.$data['ex1'].'/models/'.$title.'_model.php',$model);write_file('./application/modules/'.$data['ex1'].'/controllers/'.$title.'.php',$controller);$title2 = $this->input->post("expansion2");$controller2 = '<?php class '.$title2.' extends CI_Controller{function index(){$this->load->view("'.$title2.'_view");}}';$model2 = '<?php class '.$title2.' extends CI_Model{}';$view2 = "VIEW";write_file('./application/modules/'.$data['ex2'].'/views/'.$title2.'_view.php',$view2);write_file('./application/modules/'.$data['ex2'].'/models/'.$title2.'_model.php',$model2);write_file('./application/modules/'.$data['ex2'].'/controllers/'.$title2.'.php',$controller2);	$this->db->insert("index", $data);	}		function get()	{	$query = $this->db->get("index");	if($query->num_rows() > 0)	{	$data = $query->result();	return $data;	}	}}